import React, { Component } from "react";
import cls from "classnames";
import css from "./Button.module.css";

export default class Button extends Component {
  render() {
    const { appearance = "primary", click, children } = this.props;
    return (
      <button
        className={cls(css.btn, css[appearance])}
        onClick={(ev) => (click ? click(ev) : null)}
      >
        {children}
      </button>
    );
  }
}
