import React, { Component } from "react";
import cls from "classnames";
import css from "./IconButton.module.css";

export default class IconButton extends Component {
  render() {
    const { appearance = "primary", icon, click } = this.props;
    return (
      <button
        className={cls(css.button, css[appearance])}
        onClick={(ev) => (click ? click(ev) : null)}
      >
        {icon && <span className={cls(css.icon, icon)} />}
      </button>
    );
  }
}
