import React, { Component } from "react";
import css from "./Slider.module.css";

export default class Slider extends Component {
  render() {
    const {
      value = 0,
      label = "",
      min = 0,
      max = 100,
      step = 1,
      change,
    } = this.props;

    const inactive = "#dbdbdb";

    const progress = ((value - min) / (max - min)) * 100 + "%";
    const bgStyle = {
      background: `linear-gradient(90deg, var(--wx-input-focus-color) 0% ${progress}, ${inactive} ${progress} 100%)`,
    };

    return (
      <div className={css.slider}>
        <label className={css.label}>{label}</label>
        <input
          type="range"
          className={css.range}
          style={bgStyle}
          min={min}
          max={max}
          step={step}
          value={value}
          onChange={(e) => (change ? change(e.target.value) : null)}
        />
      </div>
    );
  }
}
